mapper.sql:
/* procSearch */

    AND `no` = #{no}





                AND LOWER(`name`) LIKE concat('%', #{searchKeyword},'%')


                AND LOWER(summary) LIKE concat('%', #{searchKeyword},'%')


                AND (LOWER(`name`) LIKE concat('%', #{searchKeyword},'%') OR LOWER(summary) LIKE concat('%', #{searchKeyword},'%'))


                AND 1=1







            AND use_yn = #{searchTypeUseYn}


            AND 1=1

mapper.select:
/* Catalog.getBuildPackCatalogList */ SELECT , bc.buildpack_name AS buildPackName, (SELECT cd.value FROM code_detail cd WHERE cd.key = bc.classification AND cd.group_id = 'BUILD_PACK_CATALOG') AS classificationValue, (SELECT cd.summary FROM code_detail cd WHERE cd.key = bc.classification AND cd.group_id = 'BUILD_PACK_CATALOG') AS classificationSummary, bc.app_sample_file_name AS appSampleFileName, bc.app_sample_file_path AS appSampleFilePath, bc.app_sample_file_size AS appSampleFileSize FROM buildpack_category bc WHERE 1=1
AND bc.buildpack_name = #{buildPackName} AND (bc.app_sample_file_path != null OR bc.app_sample_file_path != '')
ORDER BY bc.no DESC

/* Catalog.getBuildPackCatalogCount */
SELECT
    COUNT(*)
FROM buildpack_category bc
WHERE bc.name = #{name}

/* Catalog.getServicePackCatalogCount */
SELECT
    COUNT(*)
FROM servicepack_category sc
WHERE sc.name = #{name}

/* Catalog.getCheckDeleteBuildPackCatalogCount */
SELECT
    COUNT(*)
FROM starter_buildpack_relation sbr
WHERE sbr.buildpack_category_no = #{no}

/* Catalog.getCheckDeleteServicePackCatalogCount */
SELECT
    COUNT(*)
FROM starter_servicepack_relation ssr
WHERE ssr.servicepack_category_no = #{no}

/* Catalog.getBuildPackCatalogCount */
SELECT
    COUNT(*)
FROM starter_category sc
WHERE sc.name = #{name}

/* Catalog.getSelectedServicePackList */
SELECT
    ssr.servicepack_category_no
FROM starter_servicepack_relation ssr
WHERE ssr.starter_category_no = ${no}
ORDER BY ssr.servicepack_category_no

mapper.insert:
/* Catalog.insertSelectedServicePackList */
INSERT INTO starter_servicepack_relation (starter_category_no, servicepack_category_no)
VALUES((SELECT no FROM starter_category ORDER BY created DESC limit 1), #{selectedServicePackCategoryNoListValue});

/* Catalog.insertSelectedServicePackList */
INSERT INTO starter_servicepack_relation (starter_category_no, servicepack_category_no)
VALUES(#{param.no}, #{selectedServicePackCategoryNoListValue});

mapper.delete:
/* Catalog.deleteStarterCatalog */
WITH ins1 AS (
    DELETE FROM starter_category
    WHERE 1=1


        AND `no` = #{no}
        RETURNING no


) DELETE FROM STARTER_BUILDPACK_RELATION
  WHERE starter_category_no = (SELECT no FROM ins1);

/* Catalog.insertSelectedServicePackList */
DELETE FROM starter_servicepack_relation
WHERE starter_category_no = #{starterCategoryNo}

mapper.select:
/* Catalog.getCatalogHistoryList */ SELECT MAX(ch.no) AS "no", ch.catalog_no AS catalogNo, ch.catalog_type AS catalogType, MAX(ch.user_id) AS userId, MAX(ch.created) AS created, MAX(ch.lastModified) AS lastModified, COALESCE( CASE ch.catalog_type WHEN 'starter' THEN (SELECT sc.name FROM starter_category sc WHERE sc.no = ch.catalog_no) WHEN 'buildPack' THEN (SELECT bc.name FROM buildpack_category bc WHERE bc.no = ch.catalog_no) WHEN 'servicePack' THEN (SELECT svc.name FROM servicepack_category svc WHERE svc.no = ch.catalog_no) ELSE NULL END, '' ) AS "name", COALESCE( CASE ch.catalog_type WHEN 'starter' THEN (SELECT sc.summary FROM starter_category sc WHERE sc.no = ch.catalog_no) WHEN 'buildPack' THEN (SELECT bc.summary FROM buildpack_category bc WHERE bc.no = ch.catalog_no) WHEN 'servicePack' THEN (SELECT svc.summary FROM servicepack_category svc WHERE svc.no = ch.catalog_no) ELSE NULL END, '' ) AS summary, COALESCE( CASE ch.catalog_type WHEN 'starter' THEN (SELECT sc.description FROM starter_category sc WHERE sc.no = ch.catalog_no) WHEN 'buildPack' THEN (SELECT bc.description FROM buildpack_category bc WHERE bc.no = ch.catalog_no) WHEN 'servicePack' THEN (SELECT svc.description FROM servicepack_category svc WHERE svc.no = ch.catalog_no) ELSE NULL END, '' ) AS description, COALESCE( CASE ch.catalog_type WHEN 'starter' THEN (SELECT sc.thumb_img_path FROM starter_category sc WHERE sc.no = ch.catalog_no) WHEN 'buildPack' THEN (SELECT bc.thumb_img_path FROM buildpack_category bc WHERE bc.no = ch.catalog_no) WHEN 'servicePack' THEN (SELECT svc.thumb_img_path FROM servicepack_category svc WHERE svc.no = ch.catalog_no) ELSE NULL END, '' ) AS thumbImgPath FROM catalog_history ch WHERE 1=1
AND ch.no = #{no}
ch.catalog_no = #{catalogNo},
ch.catalog_type = #{catalogType},
GROUP BY ch.catalog_no, ch.catalog_type ORDER BY `no` DESC LIMIT #{limitSize}

/* Catalog.getCatalogStarterRelationBuildPackList */ SELECT , bc.buildpack_name AS buildPackName, (SELECT cd.value FROM code_detail cd WHERE cd.key = bc.classification AND cd.group_id = 'BUILD_PACK_CATALOG') AS classificationValue, (SELECT cd.summary FROM code_detail cd WHERE cd.key = bc.classification AND cd.group_id = 'BUILD_PACK_CATALOG') AS classificationSummary, bc.app_sample_file_name AS appSampleFileName, bc.app_sample_file_path AS appSampleFilePath, bc.app_sample_file_size AS appSampleFileSize FROM buildpack_category bc WHERE 1=1
AND bc.no = (SELECT sbr.buildpack_category_no FROM starter_buildpack_relation sbr WHERE sbr.starter_category_no = #{catalogNo})
ORDER BY bc.no DESC

/* Catalog.getCatalogStarterRelationServicePackList */ SELECT , sc.service_name AS servicePackName, (SELECT cd.value FROM code_detail cd WHERE cd.key = sc.classification AND cd.group_id = 'SERVICE_PACK_CATALOG') AS classificationValue, (SELECT cd.summary FROM code_detail cd WHERE cd.key = sc.classification AND cd.group_id = 'SERVICE_PACK_CATALOG') AS classificationSummary, sc.parameter AS parameter FROM servicepack_category sc WHERE 1=1
AND EXISTS (SELECT ssr.servicepack_category_no FROM starter_servicepack_relation ssr WHERE ssr.starter_category_no = #{catalogNo} AND sc.no = ssr.servicepack_category_no)
ORDER BY sc.no DESC
