mapper.insert:
  /* WebIdeUser.insertUser */
 INSERT INTO web_ide_user(
            user_id
          , org_name
          , created_at
          )
  VALUES (
           #{userId}
          , #{orgName}
          , now()
);

mapper.delete:
/* WebIdeUser.deleteUser */
DELETE FROM web_ide_user
WHERE org_name = #{orgName}

mapper.update:
/* WebIdeUser.updateUser */
UPDATE web_ide_user
SET updated_at = now()

    ,url = #{url}


    ,use_yn = #{useYn}


WHERE user_id = #{userId} and
org_name = #{orgName}
